package com.orion.math.geometry.shape.triangle;

import static org.junit.jupiter.api.Assertions.assertTrue;

import com.orion.math.number.ANumber;
import org.junit.jupiter.api.Test;

// @RunWith(ConcurrentJUnitRunner.class)
public class TriangleTest
{
    @Test
    public void test_triangle1()
    {
        Triangle result = Triangle.of(ANumber.of(0.5), ANumber.of(2).getSquareRoot());
        assertTrue(ANumber.of(
                        "45.")
                        .equal(result.getAngleAAsDegrees()));
        assertTrue(ANumber.of(
                        "89.")
                        .equal(result.getAngleBAsDegrees()));
        assertTrue(ANumber.of(
                        "44.")
                        .equal(result.getAngleCAsDegrees()));
        assertTrue(ANumber.of("1.0000000060168657").equal(result.getLengthOfA()));
        assertTrue(ANumber.of("1.4142135623730951").equal(result.getLengthOfB()));
        assertTrue(ANumber.of("0.9999999939831343").equal(result.getLengthOfC()));
    }
}